import React, { useState } from "react";
import { MdOutlineCancel } from "react-icons/md";
import "./InputComponent.css"
function InputComponent() {
  const [selectedItem, setSelectedItem] = useState([]);
  const [listItem, setListItem] = useState([
    { id: 1,name:"Tanishk", email: "tanishk@gmail.com", pic: "https://w0.peakpx.com/wallpaper/613/983/HD-wallpaper-itachi-uchiha-sharingan-artwork-naruto-uchiha-itachi-naruto-characters.jpg" },
    { id: 2,name:"Saqlain Ali", email: "ali@gmail.com", pic: "https://w0.peakpx.com/wallpaper/826/952/HD-wallpaper-obito-uchiha-anime-characters-manga-naruto.jpg" },
    { id: 3,name:"Umang", email: "umang@gmail.com", pic: "https://w0.peakpx.com/wallpaper/628/394/HD-wallpaper-itachi-uchiha-characters-manga-naruto.jpg" },
    { id: 4,name:"Manish", email: "manish@gmail.com", pic: "https://w0.peakpx.com/wallpaper/782/768/HD-wallpaper-konan-rain-naruto-manga-artwork-naruto-characters.jpg" },
    { id: 5,name:"Pavitra", email: "pavitra@gmail.com", pic: "https://w0.peakpx.com/wallpaper/57/476/HD-wallpaper-hinata-hyuga-naruto-characters-hinata-uzumaki-manga-artwork-naruto.jpg" },
    { id: 5,name:"Shashwat", email: "shashu@gmail.com", pic: "https://w0.peakpx.com/wallpaper/160/1011/HD-wallpaper-sasuke-uchiha-silhouette-moon-naruto-characters-minimalism-manga-sasuke-uchiha-naruto-uchiha-sasuke.jpg" }
  ]);
  const [showList, setShowList] = useState(false);
  const handleClick = (item) =>{
    setSelectedItem([...selectedItem, item])
    setListItem(listItem.filter(i=>i.id!==item.id))
  }
  return (
    <div className="container">
      <div className="section-1">PICK USERS</div>
      <div onClick={()=>setShowList(!showList)} className="section-2">

      {selectedItem.length === 0 ? (
        <div></div>
      ) : (
        <div className="section-2-1">
          {selectedItem.map((item, index) => {
            return (
              <div key={index} className="section-2-1-1">
                <p>{item.email}</p>
                <button><MdOutlineCancel /></button>
              </div>
            );
          })}
        </div>
      )}
      </div>
      <div className="section-3">
      {showList && (
        <div className="section-3-1">

          {listItem.map((item, index) => {
            return (
              <div onClick={()=>handleClick(item)} className="section-3-1-1">
                <p className="section-3-1-1-p">
                  <img src={`${item.pic}`} className="section-3-1-1-p-img"/>
                </p>
                <p>{item.name}</p>
                <p>{item.email}</p>
              </div>
            );
          })}
        </div>
      )}
      </div>
    </div>
  );
}

export default InputComponent;
