import React, { useEffect, useRef, useState } from "react";
import ListItem from "./ListItem";

function InputComponent() {
  const [listItem, setListItem] = useState([
    {
      id: 1,
      email: "tanishk@gmail.com",
      img: "",
    },
    {
      id: 2,
      email: "ali@gmail.com",
      img: "",
    },
    {
      id: 3,
      email: "umang@gmail.com",
      img: "",
    },
    {
      id: 4,
      email: "manish@gmail.com",
      img: "",
    },
  ]);
  const [selectedItem, setSelectedItem] = useState([])
  const [showList, setShowList] = useState(false)
  const inputRef = useRef(null)
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (inputRef.current && !inputRef.current.contains(event.target)) {
        setShowList(false);
      }
    };
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return <div>
    <div  onClick={()=>setShowList(!showList)} ref={inputRef}>
      {selectedItem.length==0?<div>Enter email address</div>:<div>Not Done</div>}
    </div>
    {showList && <ListItem listItem={listItem} setSelectedItem={setSelectedItem}/>}
  </div>;
}

export default InputComponent;
