import React, { useState } from "react";
import "./InputComponent.css"
function InputComponent() {
  const [selectedItem, setSelectedItem] = useState([]);
  const [listItem, setListItem] = useState([
    { id: 1, email: "tanishk@gmail.com", img: "" },
    { id: 2, email: "ali@gmail.com", img: "" },
    { id: 3, email: "umang@gmail.com", img: "" },
  ]);
  const [showList, setShowList] = useState(false);
  const handleClick = (item) =>{
    // console.log(id)
    setSelectedItem([...selectedItem, item])
    setListItem(listItem.filter(i=>i.id!==item.id))
  }
  return (
    <div className="container">
      <div className="section-1">PICK USERS</div>
      <div onClick={()=>setShowList(!showList)} className="section-2">
      {selectedItem.length === 0 ? (
        <div>Add New user...</div>
      ) : (
        <div>
          {selectedItem.map((item, index) => {
            return (
              <div key={index}>
                <p>{item.email}</p>
              </div>
            );
          })}
        </div>
      )}
      </div>
      <div className="section-3">
      {showList && (
        <div>
          {listItem.map((item, index) => {
            return (
              <span onClick={()=>handleClick(item)}>
                <p>{item.email}</p>
              </span>
            );
          })}
        </div>
      )}
      </div>
    </div>
  );
}

export default InputComponent;
