import React, { useState } from "react";

function InputComponent() {
  const [selectedItem, setSelectedItem] = useState([]);
  const [listItem, setListItem] = useState([
    { id: 1, email: "tanishk@gmail.com", img: "" },
    { id: 2, email: "ali@gmail.com", img: "" },
    { id: 3, email: "umang@gmail.com", img: "" },
  ]);
  const [showList, setShowList] = useState(false);
  const handleClick = (id) =>{
    // console.log(id)
    setSelectedItem(listItem.filter(item=>item.id===id))
    setListItem(listItem.filter(item=>item.id!==id))
  }
  return (
    <div onClick={()=>setShowList(!showList)}>
      {selectedItem.length === 0 ? (
        <div>Enter Email Address</div>
      ) : (
        <div>
          {selectedItem.map((item, index) => {
            return (
              <div key={index}>
                <p>{item.email}</p>
              </div>
            );
          })}
        </div>
      )}
      {showList && (
        <div>
          {listItem.map((item, index) => {
            return (
              <div onClick={()=>handleClick(item.id)}>
                <p>{item.email}</p>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

export default InputComponent;
